PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "_prisma_migrations" (
    "id"                    TEXT PRIMARY KEY NOT NULL,
    "checksum"              TEXT NOT NULL,
    "finished_at"           DATETIME,
    "migration_name"        TEXT NOT NULL,
    "logs"                  TEXT,
    "rolled_back_at"        DATETIME,
    "started_at"            DATETIME NOT NULL DEFAULT current_timestamp,
    "applied_steps_count"   INTEGER UNSIGNED NOT NULL DEFAULT 0
);
CREATE TABLE IF NOT EXISTS "users" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "email" TEXT NOT NULL,
    "fullName" TEXT NOT NULL,
    "phoneNumber" TEXT,
    "password" TEXT,
    "role" TEXT NOT NULL DEFAULT 'CITIZEN',
    "wardId" TEXT,
    "department" TEXT,
    "language" TEXT NOT NULL DEFAULT 'en',
    "avatar" TEXT,
    "isActive" BOOLEAN NOT NULL DEFAULT true,
    "lastLogin" DATETIME,
    "joinedOn" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "createdAt" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "updatedAt" DATETIME NOT NULL,
    CONSTRAINT "users_wardId_fkey" FOREIGN KEY ("wardId") REFERENCES "wards" ("id") ON DELETE SET NULL ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "wards" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "name" TEXT NOT NULL,
    "description" TEXT,
    "isActive" BOOLEAN NOT NULL DEFAULT true,
    "createdAt" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "updatedAt" DATETIME NOT NULL
);
CREATE TABLE IF NOT EXISTS "sub_zones" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "name" TEXT NOT NULL,
    "wardId" TEXT NOT NULL,
    "description" TEXT,
    "isActive" BOOLEAN NOT NULL DEFAULT true,
    "createdAt" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "updatedAt" DATETIME NOT NULL,
    CONSTRAINT "sub_zones_wardId_fkey" FOREIGN KEY ("wardId") REFERENCES "wards" ("id") ON DELETE CASCADE ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "complaints" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "complaintId" TEXT,
    "title" TEXT,
    "description" TEXT NOT NULL,
    "type" TEXT NOT NULL,
    "status" TEXT NOT NULL DEFAULT 'REGISTERED',
    "priority" TEXT NOT NULL DEFAULT 'MEDIUM',
    "slaStatus" TEXT NOT NULL DEFAULT 'ON_TIME',
    "wardId" TEXT NOT NULL,
    "subZoneId" TEXT,
    "area" TEXT NOT NULL,
    "landmark" TEXT,
    "address" TEXT,
    "coordinates" TEXT,
    "contactName" TEXT,
    "contactEmail" TEXT,
    "contactPhone" TEXT NOT NULL,
    "isAnonymous" BOOLEAN NOT NULL DEFAULT false,
    "submittedById" TEXT,
    "assignedToId" TEXT,
    "resolvedById" TEXT,
    "submittedOn" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "assignedOn" DATETIME,
    "resolvedOn" DATETIME,
    "closedOn" DATETIME,
    "deadline" DATETIME,
    "remarks" TEXT,
    "citizenFeedback" TEXT,
    "rating" INTEGER,
    "tags" TEXT,
    "createdAt" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "updatedAt" DATETIME NOT NULL,
    CONSTRAINT "complaints_wardId_fkey" FOREIGN KEY ("wardId") REFERENCES "wards" ("id") ON DELETE RESTRICT ON UPDATE CASCADE,
    CONSTRAINT "complaints_subZoneId_fkey" FOREIGN KEY ("subZoneId") REFERENCES "sub_zones" ("id") ON DELETE SET NULL ON UPDATE CASCADE,
    CONSTRAINT "complaints_submittedById_fkey" FOREIGN KEY ("submittedById") REFERENCES "users" ("id") ON DELETE SET NULL ON UPDATE CASCADE,
    CONSTRAINT "complaints_assignedToId_fkey" FOREIGN KEY ("assignedToId") REFERENCES "users" ("id") ON DELETE SET NULL ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "status_logs" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "complaintId" TEXT NOT NULL,
    "userId" TEXT NOT NULL,
    "fromStatus" TEXT,
    "toStatus" TEXT NOT NULL,
    "comment" TEXT,
    "timestamp" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    CONSTRAINT "status_logs_complaintId_fkey" FOREIGN KEY ("complaintId") REFERENCES "complaints" ("id") ON DELETE CASCADE ON UPDATE CASCADE,
    CONSTRAINT "status_logs_userId_fkey" FOREIGN KEY ("userId") REFERENCES "users" ("id") ON DELETE RESTRICT ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "attachments" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "complaintId" TEXT NOT NULL,
    "fileName" TEXT NOT NULL,
    "originalName" TEXT NOT NULL,
    "mimeType" TEXT NOT NULL,
    "size" INTEGER NOT NULL,
    "url" TEXT NOT NULL,
    "uploadedAt" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    CONSTRAINT "attachments_complaintId_fkey" FOREIGN KEY ("complaintId") REFERENCES "complaints" ("id") ON DELETE CASCADE ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "notifications" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "userId" TEXT NOT NULL,
    "complaintId" TEXT,
    "serviceRequestId" TEXT,
    "type" TEXT NOT NULL,
    "title" TEXT NOT NULL,
    "message" TEXT NOT NULL,
    "isRead" BOOLEAN NOT NULL DEFAULT false,
    "sentAt" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "readAt" DATETIME,
    CONSTRAINT "notifications_userId_fkey" FOREIGN KEY ("userId") REFERENCES "users" ("id") ON DELETE CASCADE ON UPDATE CASCADE,
    CONSTRAINT "notifications_complaintId_fkey" FOREIGN KEY ("complaintId") REFERENCES "complaints" ("id") ON DELETE CASCADE ON UPDATE CASCADE,
    CONSTRAINT "notifications_serviceRequestId_fkey" FOREIGN KEY ("serviceRequestId") REFERENCES "service_requests" ("id") ON DELETE CASCADE ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "messages" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "complaintId" TEXT NOT NULL,
    "sentById" TEXT NOT NULL,
    "receivedById" TEXT,
    "content" TEXT NOT NULL,
    "isInternal" BOOLEAN NOT NULL DEFAULT true,
    "sentAt" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "readAt" DATETIME,
    CONSTRAINT "messages_complaintId_fkey" FOREIGN KEY ("complaintId") REFERENCES "complaints" ("id") ON DELETE CASCADE ON UPDATE CASCADE,
    CONSTRAINT "messages_sentById_fkey" FOREIGN KEY ("sentById") REFERENCES "users" ("id") ON DELETE RESTRICT ON UPDATE CASCADE,
    CONSTRAINT "messages_receivedById_fkey" FOREIGN KEY ("receivedById") REFERENCES "users" ("id") ON DELETE SET NULL ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "otp_sessions" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "userId" TEXT,
    "email" TEXT NOT NULL,
    "phoneNumber" TEXT,
    "otpCode" TEXT NOT NULL,
    "purpose" TEXT NOT NULL DEFAULT 'GUEST_VERIFICATION',
    "isVerified" BOOLEAN NOT NULL DEFAULT false,
    "expiresAt" DATETIME NOT NULL,
    "createdAt" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "verifiedAt" DATETIME,
    CONSTRAINT "otp_sessions_userId_fkey" FOREIGN KEY ("userId") REFERENCES "users" ("id") ON DELETE CASCADE ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "departments" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "name" TEXT NOT NULL,
    "description" TEXT,
    "headUserId" TEXT,
    "isActive" BOOLEAN NOT NULL DEFAULT true,
    "createdAt" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "updatedAt" DATETIME NOT NULL
);
CREATE TABLE IF NOT EXISTS "system_config" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "key" TEXT NOT NULL,
    "value" TEXT NOT NULL,
    "description" TEXT,
    "isActive" BOOLEAN NOT NULL DEFAULT true,
    "updatedAt" DATETIME NOT NULL
);
CREATE TABLE IF NOT EXISTS "service_requests" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "title" TEXT,
    "serviceType" TEXT NOT NULL,
    "description" TEXT NOT NULL,
    "status" TEXT NOT NULL DEFAULT 'SUBMITTED',
    "priority" TEXT NOT NULL DEFAULT 'NORMAL',
    "wardId" TEXT NOT NULL,
    "area" TEXT NOT NULL,
    "address" TEXT NOT NULL,
    "landmark" TEXT,
    "contactName" TEXT NOT NULL,
    "contactEmail" TEXT NOT NULL,
    "contactPhone" TEXT NOT NULL,
    "submittedById" TEXT,
    "assignedToId" TEXT,
    "submittedOn" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "preferredDateTime" DATETIME,
    "assignedOn" DATETIME,
    "expectedCompletion" DATETIME,
    "completedOn" DATETIME,
    "remarks" TEXT,
    "citizenFeedback" TEXT,
    "rating" INTEGER,
    "createdAt" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "updatedAt" DATETIME NOT NULL,
    CONSTRAINT "service_requests_wardId_fkey" FOREIGN KEY ("wardId") REFERENCES "wards" ("id") ON DELETE RESTRICT ON UPDATE CASCADE,
    CONSTRAINT "service_requests_submittedById_fkey" FOREIGN KEY ("submittedById") REFERENCES "users" ("id") ON DELETE SET NULL ON UPDATE CASCADE,
    CONSTRAINT "service_requests_assignedToId_fkey" FOREIGN KEY ("assignedToId") REFERENCES "users" ("id") ON DELETE SET NULL ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "service_request_status_logs" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "serviceRequestId" TEXT NOT NULL,
    "userId" TEXT NOT NULL,
    "fromStatus" TEXT,
    "toStatus" TEXT NOT NULL,
    "comment" TEXT,
    "timestamp" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    CONSTRAINT "service_request_status_logs_serviceRequestId_fkey" FOREIGN KEY ("serviceRequestId") REFERENCES "service_requests" ("id") ON DELETE CASCADE ON UPDATE CASCADE,
    CONSTRAINT "service_request_status_logs_userId_fkey" FOREIGN KEY ("userId") REFERENCES "users" ("id") ON DELETE RESTRICT ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "reports" (
    "id" TEXT NOT NULL PRIMARY KEY,
    "name" TEXT NOT NULL,
    "type" TEXT NOT NULL,
    "filters" TEXT NOT NULL,
    "data" TEXT NOT NULL,
    "generatedBy" TEXT NOT NULL,
    "generatedAt" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP
);
CREATE UNIQUE INDEX "users_email_key" ON "users"("email");
CREATE UNIQUE INDEX "wards_name_key" ON "wards"("name");
CREATE UNIQUE INDEX "complaints_complaintId_key" ON "complaints"("complaintId");
CREATE INDEX "complaints_submittedById_createdAt_idx" ON "complaints"("submittedById", "createdAt");
CREATE INDEX "complaints_wardId_status_idx" ON "complaints"("wardId", "status");
CREATE INDEX "complaints_assignedToId_status_idx" ON "complaints"("assignedToId", "status");
CREATE INDEX "complaints_type_status_idx" ON "complaints"("type", "status");
CREATE INDEX "complaints_priority_status_idx" ON "complaints"("priority", "status");
CREATE INDEX "complaints_submittedOn_idx" ON "complaints"("submittedOn");
CREATE INDEX "status_logs_complaintId_timestamp_idx" ON "status_logs"("complaintId", "timestamp");
CREATE INDEX "notifications_userId_isRead_idx" ON "notifications"("userId", "isRead");
CREATE INDEX "notifications_complaintId_idx" ON "notifications"("complaintId");
CREATE INDEX "notifications_serviceRequestId_idx" ON "notifications"("serviceRequestId");
CREATE INDEX "otp_sessions_email_purpose_isVerified_idx" ON "otp_sessions"("email", "purpose", "isVerified");
CREATE INDEX "otp_sessions_expiresAt_idx" ON "otp_sessions"("expiresAt");
CREATE UNIQUE INDEX "departments_name_key" ON "departments"("name");
CREATE UNIQUE INDEX "system_config_key_key" ON "system_config"("key");
CREATE INDEX "service_requests_submittedById_createdAt_idx" ON "service_requests"("submittedById", "createdAt");
CREATE INDEX "service_requests_wardId_status_idx" ON "service_requests"("wardId", "status");
CREATE INDEX "service_requests_assignedToId_status_idx" ON "service_requests"("assignedToId", "status");
CREATE INDEX "service_requests_serviceType_status_idx" ON "service_requests"("serviceType", "status");
CREATE INDEX "service_requests_submittedOn_idx" ON "service_requests"("submittedOn");
CREATE INDEX "service_request_status_logs_serviceRequestId_timestamp_idx" ON "service_request_status_logs"("serviceRequestId", "timestamp");
CREATE INDEX "attachments_complaintId_idx" ON "attachments"("complaintId");
CREATE INDEX "complaints_status_createdAt_idx" ON "complaints"("status", "createdAt");
CREATE INDEX "complaints_type_createdAt_idx" ON "complaints"("type", "createdAt");
CREATE INDEX "messages_complaintId_idx" ON "messages"("complaintId");
CREATE INDEX "messages_sentById_idx" ON "messages"("sentById");
CREATE INDEX "otp_sessions_userId_idx" ON "otp_sessions"("userId");
CREATE INDEX "reports_type_generatedAt_idx" ON "reports"("type", "generatedAt");
CREATE INDEX "reports_generatedBy_idx" ON "reports"("generatedBy");
CREATE INDEX "service_request_status_logs_userId_idx" ON "service_request_status_logs"("userId");
CREATE INDEX "status_logs_userId_idx" ON "status_logs"("userId");
CREATE INDEX "sub_zones_wardId_idx" ON "sub_zones"("wardId");
CREATE INDEX "system_config_key_isActive_idx" ON "system_config"("key", "isActive");
CREATE INDEX "users_role_isActive_idx" ON "users"("role", "isActive");
CREATE INDEX "users_wardId_idx" ON "users"("wardId");
COMMIT;
